INSERT IGNORE INTO `POST` (`ID`, `REFID`, `TITLE`, `SLUG`, `ID_POST_TYPE`, `ID_POST_CATEGORY`, `IMAGE`, `META_KEYWORDS`, `META_DESCRIPTION`, `EXCERPT`, `CONTENT`, `TAGS`, `AUTHOR`, `EXTERNAL_LINK`, `ID_TEMPLATE`, `CREATION_DATE`, `LATEST_UPDATE`, `IS_FEATURED`, `NBR_VIEWS`, `GUID`, `JSON`, `UDID`, `WFID`, `UUID`) VALUES (5,NULL,'Google Polymer – How It’ll Change the Way Web Apps are Built','google-polymer-–-how-it’ll-change-the-way-web-apps-are-built','article',36,'post/image/google-polymer.jpg','','','Along with <a href=\"http://www.hongkiat.com/blog/using-google-photos/\">Google Photos</a>, Google has also rebuilt <a href=\"https://www.polymer-project.org/1.0/\" rel=\"external\">Polymer</a> from scratch, addressing performance improvement and efficiency.','<p>Along with <a href=\"http://www.hongkiat.com/blog/using-google-photos/\">Google Photos</a>, Google has also rebuilt <a href=\"https://www.polymer-project.org/1.0/\" rel=\"external\">Polymer</a> from scratch, addressing performance improvement and efficiency. Think of Polymer as an SDK (Software Development Kit) for the Web, one which makes<strong> web application development</strong> so much faster using a new standard called <a href=\"http://w3c.github.io/webcomponents/explainer/\" rel=\"external\">Web Components</a>.</p>\r\n<h4>About Web Components</h4>\r\n<p>The best way to understand how Web Components works is by looking at the current standard elements like <code></code>. When we add <code></code> along with the URL sources of the audio, web browsers will render this element as an audio player with the play and pause button, the time rail as well as the volume slider. Ever wonder how the player controls are built and styled?</p>\r\n<p>The UI control player is hidden beneath as Shadow Roots, also known as<strong>Shadow DOM</strong>. To view Shadow DOM, launch the <strong>Chrome DevTools</strong> &gt; click on the <strong>Cog</strong> icon &gt; select the <strong>Show user agent shadow DOM</strong> option.</p>\r\n<p>In the following screenshot, you can find a stack of <code></code></p>\r\n<div> </div>\r\n<p><code></code> and <code><input type=\"text\" /></code>elements that build the UI player controls in secret.</p>','','','','blog-post',NULL,NULL,1,9.00,1,NULL,0,NULL,'899348ac-5528-40c2-8ad7-e7fd847ad62b');
INSERT IGNORE INTO `POST` (`ID`, `REFID`, `TITLE`, `SLUG`, `ID_POST_TYPE`, `ID_POST_CATEGORY`, `IMAGE`, `META_KEYWORDS`, `META_DESCRIPTION`, `EXCERPT`, `CONTENT`, `TAGS`, `AUTHOR`, `EXTERNAL_LINK`, `ID_TEMPLATE`, `CREATION_DATE`, `LATEST_UPDATE`, `IS_FEATURED`, `NBR_VIEWS`, `GUID`, `JSON`, `UDID`, `WFID`, `UUID`) VALUES (6,NULL,'10 Useful Image Tools Every Social Marketer Needs','10-useful-image-tools-every-social-marketer-needs','article',47,'post/image/social-media-imaging-tools.jpg','','','If you work with social media a lot, you must know that every platform requires different dimensions for the photos you upload.','<p>If you work with social media a lot, you must know that every platform requires different dimensions for the photos you upload. These dimensions ensure that you can create the best-looking photo for that position or location, be it a<a href=\"http://www.hongkiat.com/blog/creative-facebook-timeline-covers/\">Facebook cover photo</a> or a <a href=\"http://www.hongkiat.com/blog/google-plus-cover-photos/\">cover photo for Google Plus</a>, a Twitter header or more.</p>\r\n<p>To do this manually is a pointless venture because now there are plenty of tools that can help you manipulate your images into the dimensions and sizes suitable for most, if not all social networking sites out there. Here are 10 great tools that have help you better manage your images for higher social engagement online.</p>','designer','','','blog-post',NULL,NULL,1,2.00,1,'{\"POST_SECTION\":[{\"ID\":\"3\",\"REFID\":null,\"TITLE\":\"1. Social Image Resizer Tool\",\"ID_POST\":\"6\",\"TAG_LINE\":\"\",\"IMAGE\":\"postsection\\/image\\/social-image-resizer-tool.jpg\",\"BACKGROUND_IMAGE\":\"\",\"BACKGROUND_COLOR\":\"\",\"VIDEO\":\"\",\"CONTENT\":\"Social media is fickle-minded. There are so many image size rules for so many social networking sites that it will be a surprise if you can keep track of them all e.g. Facebook requires 315px by 851px for its cover photo, while Twitter requires 1500px by 500px.\",\"LINK_PATH\":\"\",\"ORDERING\":\"0\",\"HIERACHY\":null,\"GUID\":\"1\",\"JSON\":null,\"UDID\":\"0\",\"WFID\":null,\"UUID\":\"f25bbd0d-f7ea-44f3-b4be-ef76f13d6d01\",\"N\":1}]}',0,NULL,'8579389d-098e-4312-a8f3-6df6f38c3b57');
