INSERT IGNORE INTO `PAGE` (`ID`, `REFID`, `NAME`, `TITLE`, `PARENT`, `SLUG`, `TAG_LINE`, `THUMB`, `BACKGROUND_IMAGE`, `IMAGE`, `VIDEO`, `META_KEYWORDS`, `META_DESCRIPTION`, `INTRODUCTION`, `CONTENT`, `FOOTER`, `ID_TEMPLATE`, `ADDITIONAL_CSS`, `CUSTOM_TOP_ID_MENU`, `CUSTOM_SIDE_ID_MENU`, `VIEW_MORE_TITLE`, `VIEW_MORE_ID_PAGE`, `VIEW_MORE_LINK`, `LATEST_UPDATE`, `NBR_VIEWS`, `GUID`, `JSON`, `UDID`, `WFID`, `UUID`, `HIERACHY`) VALUES (11,'PGE0000000013','Distinguished Scientists','Distinguished Scientists',10,'distinguished-scientists','','','','','','','','','<h2>2014 Distinguished Scientists</h2>\r\n<p>They are the best and brightest from all over the world, working tirelessly right here in the heartland to discover treatments and cures for life-threatening diseases. They are at the center of discovery, conducting breakthrough research that will improve the lives of Nebraskans and beyond. They are <a href=\"http://www.unmc.edu/news.cfm?match=16051&amp;pk_campaign=email&amp;pk_kwd=Tatiana_Bronich_PhD_named_2014_Scientist_Laureate\">UNMC\'s Distinguished Scientists</a>.</p>\r\n<h3>Scientist Laureate</h3>\r\n<p>This award is the highest honor UNMC bestows to researchers.</p>\r\n<p><img title=\"2014 Scientist Laureate Tatiana Bronich, Ph.D.\" src=\"http://www.unmc.edu/research/distinguished-scientists/2014-img/bronich.jpg\" alt=\"Tatiana Bronich, Ph.D.\" width=\"280\" height=\"187\" border=\"0\" /><br /><a href=\"http://www.unmc.edu/news.cfm?match=16416\">Tatiana Bronich, Ph.D.</a></p>','','page-left-right','','','','',NULL,'',NULL,NULL,1,NULL,0,NULL,'6184c23c-8ca0-4008-9564-1822a87b69f0','-1-10-');
INSERT IGNORE INTO `PAGE` (`ID`, `REFID`, `NAME`, `TITLE`, `PARENT`, `SLUG`, `TAG_LINE`, `THUMB`, `BACKGROUND_IMAGE`, `IMAGE`, `VIDEO`, `META_KEYWORDS`, `META_DESCRIPTION`, `INTRODUCTION`, `CONTENT`, `FOOTER`, `ID_TEMPLATE`, `ADDITIONAL_CSS`, `CUSTOM_TOP_ID_MENU`, `CUSTOM_SIDE_ID_MENU`, `VIEW_MORE_TITLE`, `VIEW_MORE_ID_PAGE`, `VIEW_MORE_LINK`, `LATEST_UPDATE`, `NBR_VIEWS`, `GUID`, `JSON`, `UDID`, `WFID`, `UUID`, `HIERACHY`) VALUES (12,'PGE0000000014','Patient Care','Patient Care',1,'patient-care','','','','page/image/patient-care-header-ear.jpg','','','','','<p>As an academic health science center, UNMC offers patients world-class health care, backed by the latest research innovations and practiced by faculty training the next generation of health providers.</p>\r\n<p>With our hospital partner, <a href=\"http://www.nebraskamed.com/\">Nebraska Medicine</a>, UNMC provides services in about 50 specialties and subspecialties, including cancer, neurosciences, heart disease and others.</p>\r\n<p>In addition to their research and education responsibilities, our medical faculty practice through Nebraska Medicine, which includes hospitals in Omaha and Bellevue, as well as about 40 ambulatory clinics throughout Omaha. The 125-practitioner <a href=\"http://www.childrensspecialtyphysicians.org/\" target=\"_blank\">Children\'s Specialty Physicians</a> combines the academic resources of UNMC and the clinical resources of <a href=\"http://childrensomaha.org/\" target=\"_self\">Children’s Hospital and Medical Center</a>.  Our experts also provide <a href=\"http://www.unmc.edu/dentistry/faculty/index.html\" target=\"_self\">dental care</a> and services in <a href=\"http://www.unmc.edu/mmi/\" target=\"_self\">physical and developmental disabilities</a>.</p>\r\n<p>Through this unique combination of academic, scientific, and health care experience, we transform the discoveries of the laboratory and theory of the classroom into breakthroughs for your health.</p>','','site-section','',''