INSERT IGNORE INTO `PAGE` (`ID`, `REFID`, `NAME`, `TITLE`, `PARENT`, `SLUG`, `TAG_LINE`, `THUMB`, `BACKGROUND_IMAGE`, `IMAGE`, `VIDEO`, `META_KEYWORDS`, `META_DESCRIPTION`, `INTRODUCTION`, `CONTENT`, `FOOTER`, `ID_TEMPLATE`, `ADDITIONAL_CSS`, `CUSTOM_TOP_ID_MENU`, `CUSTOM_SIDE_ID_MENU`, `VIEW_MORE_TITLE`, `VIEW_MORE_ID_PAGE`, `VIEW_MORE_LINK`, `LATEST_UPDATE`, `NBR_VIEWS`, `GUID`, `JSON`, `UDID`, `WFID`, `UUID`, `HIERACHY`) VALUES (16,'PGE0000000018','Outreach','Outreach',1,'outreach','','','','page/image/outreach-header.jpg','','','','','<p>As the only public academic health science center in the state, UNMC embraces the responsibility to reach out to all Nebraskans.</p>\r\n<p>Whether it is <a href=\"http://www.unmc.edu/patientcare/index.html\">patient care</a>, <a href=\"http://www.unmc.edu/education/index.html\">education</a> or research-related, UNMC extends its health care experts to the community through pediatric behavioral health clinics, Science Cafes, cancer screenings, distance education and more.</p>\r\n<p>UNMC strives to be a source people can trust<strong>.</strong> With programs like CHIRS, (Consumer Health Information Resource Service) all Nebraska residents have free access – pending or following a diagnosis -- to the latest medical information through UNMC’s McGoogan Library of Medicine.</p>\r\n<p>Access for all is a priority at UNMC. With health disparities a statewide concern, UNMC takes a proactive approach to recruit students and residents to practice in rural, underserved areas through the Rural Health Opportunities Program (RHOP) and the Residency Rural Training Track.</p>\r\n<p>UNMC fully supports any and all programs that lead to affordable and quality health care, education, information, resources and services for everyone.</p>','','site-section','','','','',NULL,'',NULL,1.00,1,'{\"MENU_ITEM\":[{\"ID\":\"4\",\"REFID\":null,\"TITLE\":\"Outreach\",\"CLASS\":\"\",\"TAG_LINE\":\"\",\"ID_MENU\":\"main-menu\",\"PARENT\":null,\"ID_PAGE\":\"16\",\"ID_POST\":null,\"ID_POST_CATEGORY\":null,\"PATH\":\"\",\"CUSTOM_CONTENT\":\"\",\"HAS_BREAK_AFTER\":\"0\",\"ORDERING\":\"0\",\"ACL_ID_USER_GROUP\":null,\"HIERACHY\":null,\"GUID\":\"1\",\"JSON\":null,\"UDID\":\"0\",\"WFID\":null,\"UUID\":\"3710ba5a-e66c-4fb5-9c44-55e18fc4e975\",\"N\":1}]}',0,NULL,'cf46830f-53f5-4df7-bab9-856a17a3f791','-1-');
INSERT IGNORE INTO `PAGE` (`ID`, `REFID`, `NAME`, `TITLE`, `PARENT`, `SLUG`, `TAG_LINE`, `THUMB`, `BACKGROUND_IMAGE`, `IMAGE`, `VIDEO`, `META_KEYWORDS`, `META_DESCRIPTION`, `INTRODUCTION`, `CONTENT`, `FOOTER`, `ID_TEMPLATE`, `ADDITIONAL_CSS`, `CUSTOM_TOP_ID_MENU`, `CUSTOM_SIDE_ID_MENU`, `VIEW_MORE_TITLE`, `VIEW_MORE_ID_PAGE`, `VIEW_MORE_LINK`, `LATEST_UPDATE`, `NBR_VIEWS`, `GUID`, `JSON`, `UDID`, `WFID`, `UUID`, `HIERACHY`) VALUES (17,'PGE0000000019','College of Dentistry','College of Dentistry',NULL,'dentistry','','','','','','','','','<p><strong>The College of Dentistry is known for its extraordinary education, scientific discovery and clinical care — resulting in improved oral health for people of all ages.</strong></p>\r\n<p>Under the direction of our exceptional faculty, students pursue a rigorous<a title=\"Programs\" href=\"http://www.unmc.edu/dentistry/programs/index.html\">course of study</a> at our nationally ranked college. <strong><a title=\"Dental Services\" href=\"http://www.unmc.edu/dentistry/patient-care/services/index.html\">Clinics</a></strong> within the college accept patients of all ages.</p>\r\n<p><strong><a title=\"Continuing Education for Dental Professionals\" href=\"http://www.unmc.edu/dentistry/continuinged/index.html\">Continuing Education</a> </strong>programs, available to practicing dentists, are offered through the UNMC\'s College of Dentistry and present updates on the newest techniques and latest research. In addition to continuing education, the College of Dentistry supports dental professionals with:</p>\r\n<ul>\r\n<li><a title=\"Head and Neck Diagnostic Service\" href=\"http://www.unmc.edu/dentistry/patient-care/services/uda/head-neck-services.html\">pathology</a> and <a title=\"Biomonitoring Service\" href=\"http://www.unmc.edu/dentistry/about/departments/oral-biology/biomonitoring-service.html\">biomonitoring</a> services</li>\r\n<li><a title=\"Research\" href=\"http://www.unmc.edu/dentistry/research/index.html\">research</a> results on a wide variety of dental topics, and</li>\r\n<li><a title=\"Nebraska Teledentistry Project\" href=\"http://www.unmc.edu/dentistry/outreach/teledentistry/index.html\">teledentistry services</a></li>\r\n</ul>','','subsite','','dentistry-main-menu','','',NULL,'',NULL,11.00,1,'{\"PAGE_GALLERY\":[{\"ID\":\"2\",\"REFID\":null,\"TITLE\":\"Tech\",\"ID_PAGE\":\"17\",\"IMAGE\":\"pagegallery\\/image\\/cod01.jpg\",\"LINK_ID_PAGE\":null,\"LINK_PATH\":\"\",\"DESCRIPTION\":\"The latest technology and advances in dentistry\\u00a0are at the heart of dental education at the UNMC College of Dentistry.\",\"ORDERING\":\"0\",\"GUID\":\"1\",\"JSON\":null,\"UDID\":\"0\",\"WFID\":null,\"UUID\":\"ab8f5a89-35d3-4e5b-bb13-e46c84208b41\",\"N\":3},{\"ID\":\"3\",\"REFID\":null,\"TITLE\":\"Entrance\",\"ID_PAGE\":\"17\",\"IMAGE\":\"pagegallery\\/image\\/college-dentistry-entrance.jpg\",\"LINK_ID_PAGE\":null,\"LINK_PATH\":\"\",\"DESCRIPTION\":\"Patients and students encounter a modern, aesthetically pleasing, comfortable environment.\",\"ORDERING\":\"0\",\"GUID\":\"1\",\"JSON\":null,\"UDID\":\"0\",\"WFID\":null,\"UUID\":\"b77a9da4-e2ae-46b7-9ee5-af1b7964cc47\",\"N\":3},{\"ID\":\"4\",\"REFID\":null,\"TITLE\":\"Patient\",\"ID_PAGE\":\"17\",\"IMAGE\":\"pagegallery\\/image\\/slide-patient.jpg\",\"LINK_ID_PAGE\":null,\"LINK_PATH\":\"\",\"DESCRIPTION\":\"We make an impact on the oral health of Nebraskans through quality patient care.\",\"ORDERING\":\"0\",\"GUID\":\"1\",\"JSON\":null,\"UDID\":\"0\",\"WFID\":null,\"UUID\":\"718f56cd-b6f1-470e-b260-24e59dc2121d\",\"N\":3}],\"PAGE_WIDGET\":[{\"ID\":\"8\",\"REFID\":null,\"ID_PAGE\":\"17\",\"TITLE\":\"Contact Us\",\"CODE\":\"contact-us\",\"LINK\":\"\",\"IMAGE\":\"\",\"ID_WIDGET_TYPE\":\"general\",\"ID_WIDGET_POSITION\":\"side-bar-left\",\"CONTENT\":\"UNMC College of Dentistry\\u00a0<br \\/>4000 East Campus Loop South<br \\/>Box 830740<br \\/>Lincoln, NE 68583-0740 USA<br \\/>phone: 402-472-1333\\r\\n<p><a href=\\\"http:\\/\\/www.unmc.edu\\/dentistry\\/